DROP TABLE IF EXISTS `players_detales`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `players_detales` (
  `player_id` int unsigned NOT NULL COMMENT 'Ссылка на игрока',
  `birthday` date DEFAULT NULL COMMENT 'Дата рождения',
  `photo_id` int unsigned DEFAULT NULL COMMENT 'Ссылка на основную фотографию пользователя',
  `nationality` varchar(100) NOT NULL COMMENT 'Национальность',
  `email` varchar(100) NOT NULL COMMENT 'Почта',
  `phone` varchar(100) NOT NULL COMMENT 'Телефон',
  `role_id` int unsigned NOT NULL COMMENT 'ссылка на амплуа',
  `created_at` datetime DEFAULT CURRENT_TIMESTAMP COMMENT 'Время создания строки',
  `updated_at` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Время обновления строки',
  PRIMARY KEY (`player_id`),
  UNIQUE KEY `nationality` (`nationality`),
  UNIQUE KEY `email` (`email`),
  UNIQUE KEY `phone` (`phone`),
  KEY `players_detales_role_id_fk` (`role_id`),
  KEY `players_detales_photo_id_fk` (`photo_id`),
  CONSTRAINT `players_detales_photo_id_fk` FOREIGN KEY (`photo_id`) REFERENCES `players_photos` (`id`),
  CONSTRAINT `players_detales_player_id_fk` FOREIGN KEY (`player_id`) REFERENCES `players` (`id`) ON DELETE CASCADE,
  CONSTRAINT `players_detales_role_id_fk` FOREIGN KEY (`role_id`) REFERENCES `roles` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='подробности про игроков';
